using System;
using System.Collections.Generic;
using System.Data.Entity;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using UnderstoodDotOrg.Domain.CommonSenseMedia.CSMReviews;


namespace UnderstoodDotOrg.Domain.Membership
{
    /// <summary>
    /// Provides a wrapper for Entity DbContext that allows custom connection string. Default code generated by Entity does not support custom connection strings
    /// </summary>
    public partial class Membership : DbContext
    {
        /// <summary>
        /// Creates a new instance of Entity DbContext for Membership using the provided connection string
        /// </summary>
        /// <param name="connectionString">Valid Entity connection string to use for this instance</param>
        public Membership(string connectionString)
            : base(connectionString)
        {
            // this will probably hurt (a lot) later. Lazy Loading means that the lookup properties need to be accessed while within an active context, 
            // which we dispose of before we hand things back through the Manager. This turns off Lazy Loading, which returns the full dataset and lookup
            // when requested, not accessed. We can revert this in the future and instead do Eager Loading (see: http://msdn.microsoft.com/en-us/data/jj574232.aspx)
            // on a per-lookup or per-object level
            this.Configuration.LazyLoadingEnabled = false;
        }

        public DbSet<CSMUserReview> CSMUserReviews { get; set; }
    }
}
